mapper.select:
select * from (

    select

    from t_project_environment_monitor_alarm_${month}
    where is_del=0

        and monitor_id = #{alarm.monitorId}


        and create_time >= #{alarm.beginTime}


        and create_time <= #{alarm.endTime}


) m
order by createTime desc

select * from (

    select
    date_format(create_time, '%Y-%m-%d %H:%i:%s') as alarmTime,

    from t_project_environment_monitor_alarm_${month}
    where is_del=0

        and monitor_id = #{alarm.monitorId}


        and create_time >= #{alarm.beginTime}


        and create_time <= #{alarm.endTime}


) m
order by createTime desc

select * from ( select 1 flag ,em.name,ma.id , pi.name as projectName , ma.alarm_info alarmInfo, ma.device_no deviceNo ,ma.`status`,DATE_FORMAT(ma.create_time ,'%Y-%m-%d %H:%i:%S')deviceTime ,( select case ma.alarm_id when 1 then concat(dtl.pm25,' ug/m³') when 2 then concat(dtl.pm25,' ug/m³') when 3 then concat(dtl.pm10,' ug/m³') when 4 then concat(dtl.pm10,' ug/m³') when 5 then concat(dtl.temperature,' ℃') when 6 then concat(dtl.temperature,' ℃') when 7 then concat(dtl.temperature,' ℃') when 8 then concat(dtl.temperature,' ℃') when 9 then concat(dtl.humidity,' %rh') when 10 then concat(dtl.humidity,' %rh') when 11 then concat(dtl.humidity,' %rh') when 12 then concat(dtl.humidity,' %rh') when 13 then concat(dtl.noise,' 分贝') when 14 then concat(dtl.noise,' 分贝') when 15 then concat(dtl.wind_speed,' m/s') when 16 then concat(dtl.wind_speed,' m/s') end from t_project_environment_monitor_detail_${month} dtl where dtl.id = ma.detail_id
) as `value` from t_project_environment_monitor_alarm_${month} ma
LEFT JOIN t_project_environment_monitor em on em.id = ma.monitor_id
LEFT JOIN t_project_info pi on em.project_id = pi.id
where ma.`status` = 0 and ma.is_del=0 and pi.uuid in (
#{item}
) union ALL
select 2 flag ,l.name,la.id , pi.name as projectName , la.alarm_info alarmInfo, la.device_no deviceNo ,la.`status` ,DATE_FORMAT(la.create_time ,'%Y-%m-%d %H:%i:%S') deviceTime
,(select case la.alarm_id when 1 then concat(dtl.weight, ' T') when 2 then concat(dtl.weight, ' T') when 3 then concat(dtl.speed,' m/s') when 4 then if(dtl.front_door_status=1,'打开','关闭') when 5 then if(dtl.back_door_status=1,'打开','关闭') end from t_project_lift_detail_${month} dtl where dtl.id = la.detail_id
) as `value` from t_project_lift_alarm_${month} la
LEFT JOIN t_project_lift l on l.id = la.lift_id
LEFT JOIN t_project_info pi on l.project_id = pi.id
where la.`status` = 0 and la.is_del=0 and pi.uuid in (
#{item}
)
UNION ALL
select 3 flag ,c.name,ca.id , pi.name as projectName , ca.alarm_info alarmInfo, ca.device_no deviceNo ,ca.`status` ,DATE_FORMAT(ca.create_time ,'%Y-%m-%d %H:%i:%S') deviceTime ,(
select case ca.alarm_id when 1 then concat(dtl.weight, ' T') when 2 then concat(dtl.weight, ' T') when 3 then concat(dtl.`range`,' m') when 4 then concat(dtl.`range`,' m') when 5 then concat(dtl.`range`,' m') when 6 then concat(dtl.`range`,' m') when 7 then concat(dtl.height,' m') when 8 then concat(dtl.height,' m') when 9 then concat(dtl.moment,' N.m') when 10 then concat(dtl.moment,' N.m') when 11 then '' when 12 then '' when 13 then '' when 14 then '' when 15 then concat(dtl.wind_speed,' m/s') when 16 then concat(dtl.tilt_angle,' °') end from t_project_crane_detail_${month} dtl where dtl.id = ca.detail_id ) as `value` from t_project_crane_alarm_${month} ca
LEFT JOIN t_project_crane c on c.id = ca.crane_id
LEFT JOIN t_project_info pi on c.project_id = pi.id
where ca.`status` = 0 and ca.is_del=0 and pi.uuid in (
#{item}
) ) m order by deviceTime desc

