mapper.select:
select
count(*)
from t_project_crane
where device_no=#{t.body.deviceNo}
and project_id=#{t.body.projectId}
and is_del=0

    and id!=#{t.body.id}

select
count(*)
from t_project_crane
where crane_no=#{t.body.craneNo}
and project_id=#{t.body.projectId}
and is_del=0

    and id!=#{t.body.id}

select
count(*)
from t_project_crane
where gprs=#{t.body.gprs}
and project_id=#{t.body.projectId}
and is_del=0

    and id!=#{t.body.id}

mapper.update:
update t_project_target_set_crane
set call_times=ifnull(call_times,0) +1
where specification =#{specification}
and manufactor =#{manufactor}

update t_project_target_set_crane
 set call_times=ifnull(call_times,0) -1
 where specification =#{specification}
 and manufactor =#{manufactor}

mapper.select:
SELECT crane.id, crane.project_id AS projectId, ( SELECT NAME FROM t_project_info WHERE t_project_info.id = crane.project_id ) AS projectName, crane.crane_no AS craneNO, crane.device_no AS deviceNo, ifnull(sum(lift_frequency),0) AS liftFrequency, work_date AS workDate FROM t_project_crane crane LEFT JOIN t_project_crane_statistics_daily daily on crane.device_no = daily.device_no where crane.is_del = 0
and crane.org_id in
#{listItem}
and crane.project_id = #{projectId}
and daily.work_date BETWEEN #{startTime} and #{endTime}
and crane.device_no=#{deviceNo}
GROUP BY deviceNo ORDER BY crane.id

SELECT
IFNULL(max(lift_frequency),0) AS liftFrequencyMax,
IFNULL(min(lift_frequency),0) AS liftFrequencyMin
FROM
t_project_crane crane
LEFT JOIN
t_project_crane_statistics_daily daily on crane.device_no = daily.device_no
where 1=1

    and crane.org_id in

        #{listItem}



    and crane.project_id = #{projectId}


    and daily.work_date BETWEEN #{startTime} and #{endTime}

and crane.device_no = #{deviceNo}
