SELECT crane.id, crane.project_id AS projectId, ( SELECT NAME FROM t_project_info WHERE t_project_info.id = crane.project_id ) AS projectName, crane.crane_no AS craneNO, crane.device_no AS deviceNo, crane.time_sum as timeSum, crane.is_online as isOnline, sum(lift_frequency) AS liftFrequency, sum(daily.percentage0) as percentage0, sum(daily.percentage40) as percentage40, sum(daily.percentage60) as percentage60, sum(daily.percentage80) as percentage80, sum(daily.percentage90) as percentage90, sum(daily.percentage110) as percentage110, sum(daily.percentage120) as percentage120, sum(daily.weight_warn) AS weightWarn, sum(daily.range_warn) AS rangeWarn, sum(daily.limit_warn) AS limitWarn, sum(daily.moment_warn) AS momentWarn, sum(daily.collision_warn) AS collisionWarn, sum(daily.weight_alarm) AS weightAlarm, sum(daily.range_alarm) AS rangeAlarm, sum(daily.limit_alarm) AS limitAlarm, sum(daily.moment_alarm) AS momentAlarm, sum(daily.collision_alarm) AS collisionAlarm, sum(daily.wind_speed_alarm) AS windSpeedAlarm, sum(daily.tilt_alarm) AS tiltAlarm FROM t_project_crane crane LEFT JOIN t_project_crane_statistics_daily daily on crane.device_no = daily.device_no
and daily.work_date BETWEEN #{startTime} and #{endTime}
where crane.is_del=0
and crane.org_id in
#{listItem}
and (crane.crane_no like #{keyWord} or crane.device_no like #{keyWord})
and crane.project_id = #{projectId}
and crane.device_no = #{deviceNo}
GROUP BY deviceNo

SELECT crane.id, crane.project_id AS projectId, ( SELECT NAME FROM t_project_info WHERE t_project_info.id = crane.project_id ) AS projectName, crane.crane_no AS craneNO, crane.device_no AS deviceNo, crane.time_sum as timeSum, crane.is_online as isOnline, sum(daily.lift_frequency) AS liftFrequency, sum(daily.weight_alarm) AS weightAlarm FROM t_project_crane crane LEFT JOIN t_project_crane_statistics_daily daily on crane.device_no = daily.device_no
and daily.work_date BETWEEN #{startTime} and #{endTime}
where crane.is_del=0
and crane.project_id = #{projectId}
and crane.org_id in
#{listItem}
and crane.device_no = #{deviceNo}
GROUP BY deviceNo
