mapper.select:
select count(*) from t_project_environment_monitor where is_del =0
and device_no = #{deviceNo}
and project_id =#{projectId}

SELECT pca.alarm_id AS alarmId,pi.name AS projectName,pi.uuid,pc.device_no AS deviceNo,pca.alarm_info AS info,COUNT(pca.id) AS amount FROM ${tableName} pca LEFT JOIN t_project_environment_monitor pc ON pc.id = pca.monitor_id LEFT JOIN t_project_info pi ON pi.id = pc.project_id WHERE pca.is_del = 0 and pc.is_del =0 AND pi.uuid IN
#{item}
AND pc.device_no = #{deviceNo}
AND pca.alarm_id = #{alarmId}
GROUP BY pc.device_no,pca.alarm_info

SELECT pca.id,pca.create_time AS deviceTime,pca.alarm_info AS info, if(pca.status =1 ,'已处理','未处理') status,
pca.modify_user_name as userName,pca.modify_time as modifyTime FROM ${tableName} pca LEFT JOIN t_project_environment_monitor pc ON pc.id = pca.monitor_id LEFT JOIN t_project_info pi ON pi.id = pc.project_id left join t_sys_user u on u.id = pca.modify_user WHERE pca.is_del = 0 and pc.is_del =0 AND pca.alarm_id = #{alarmId}
AND pc.device_no = #{deviceNo}
order by pca.create_time desc

mapper.update:
update t_project_target_set_environment
set call_times=ifnull(call_times,0) +1
where specification =#{specification}
and manufactor =#{manufactor}

update t_project_target_set_environment
set call_times=ifnull(call_times,0) -1
where specification =#{specification}
and manufactor =#{manufactor}

mapper.select:
SELECT project_id AS projectId,device_no AS deviceNo FROM t_project_environment_monitor WHERE is_del = 0 AND org_id IN
#{item}
AND status = 1 ORDER BY create_time DESC
