mapper.select:
 select 
 device_time,																				
 weight	,																				
 people	,																				
 floor	,																				
 tilt_angle	,																				
 driver,																					
 status																			

from ${tableName}
where is_del=0 and lift_id = ${id}

  and device_time between ${deviceTimeBegin} and ${deviceTimeEnd}



  and      status = ${status}


ORDER BY device_time desc

select id, lift_id, device_no, status, create_time, ifnull(end_time,now()) as end_time
from ${tableName} where lift_id = ${id}
and create_time between ${deviceTimeBegin} and ${deviceTimeEnd}
ORDER BY end_time desc
