mapper.select:
select DATE_FORMAT(
statistics_date,
'%Y-%m-%d'
)  as `xAxis` , sum(amount_used) as `yAxis` from t_project_water_daily where device_id in
(
select id from t_project_water_meter where project_id in(
select id from t_project_info where uuid = #{uuid}
)


	and	type = #{type}

)
and statistics_date >= '${date}'
GROUP BY statistics_date

select sum(l.degree-f.degree) degree from (SELECT electric_id , actual_degree degree FROM t_project_water_meter_detail_${month2} where id in( (select max(id) from t_project_water_meter_detail_${month2} where DATE_FORMAT( create_time, '%Y-%m-%d %H:%i:%s' ) LIKE '${daily2}%' AND electric_id IN ( SELECT id FROM t_project_water_meter WHERE project_id = ( SELECT id FROM t_project_info WHERE uuid = #{uuid} )
and `type` = #{type}
) GROUP BY electric_id ) )
) f
LEFT JOIN (SELECT electric_id , actual_degree degree FROM t_project_water_meter_detail_${month1} where id in( (select max(id) from t_project_water_meter_detail_${month1} where DATE_FORMAT( create_time, '%Y-%m-%d %H:%i:%s' ) LIKE '${daily1}%' AND electric_id IN ( SELECT id FROM t_project_water_meter WHERE project_id = ( SELECT id FROM t_project_info WHERE uuid = #{uuid} )
and `type` = #{type}
) GROUP BY electric_id ) )
) l on f.electric_id = l.electric_id
