mapper.update:
update t_project_target_set_water
set call_times=ifnull(call_times,0) +1
where id =#{specification}
and manufactor =#{manufactor}

update.foreach:
update t_project_target_set_water
set call_times=ifnull(call_times,0) -1
where id =#{item.specification}
and manufactor =#{item.manufactor}
